import React from 'react';
import {
  Box,
  Grid,
  Paper,
  Typography,
} from '@mui/material';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import { Bar, Line, Pie, Scatter } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const chartOptions = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: {
      position: 'top',
      labels: {
        color: 'rgba(255, 255, 255, 0.7)',
      },
    },
  },
  scales: {
    x: {
      grid: {
        color: 'rgba(255, 255, 255, 0.1)',
      },
      ticks: {
        color: 'rgba(255, 255, 255, 0.7)',
      },
    },
    y: {
      grid: {
        color: 'rgba(255, 255, 255, 0.1)',
      },
      ticks: {
        color: 'rgba(255, 255, 255, 0.7)',
      },
    },
  },
};

const PlayerAnalytics = ({ stats }) => {
  if (!stats || !stats.basicStats || !stats.detailedStats) {
    return null;
  }

  const { basicStats, detailedStats } = stats;

  // Scoring Distribution Data
  const scoringData = {
    labels: ['2PT FG', '3PT FG', 'FT'],
    datasets: [{
      label: 'Points Distribution',
      data: [
        (detailedStats.fieldGoalsPerGame - detailedStats.threePointersPerGame) * 2,
        detailedStats.threePointersPerGame * 3,
        detailedStats.freeThrowsPerGame,
      ],
      backgroundColor: [
        'rgba(255, 75, 43, 0.8)',
        'rgba(255, 142, 83, 0.8)',
        'rgba(255, 206, 86, 0.8)',
      ],
      borderColor: [
        'rgba(255, 75, 43, 1)',
        'rgba(255, 142, 83, 1)',
        'rgba(255, 206, 86, 1)',
      ],
      borderWidth: 1,
    }],
  };

  // Efficiency Metrics
  const efficiencyData = {
    labels: ['Field Goal %', 'Three Point %', 'Free Throw %'],
    datasets: [{
      label: 'Shooting Efficiency',
      data: [
        basicStats.fieldGoalPercentage,
        basicStats.threePointPercentage,
        basicStats.freeThrowPercentage,
      ],
      backgroundColor: 'rgba(255, 75, 43, 0.2)',
      borderColor: 'rgba(255, 75, 43, 1)',
      borderWidth: 2,
      fill: true,
    }],
  };

  // Performance Over Time (simulated data)
  const performanceData = {
    labels: ['Q1', 'Q2', 'Q3', 'Q4'],
    datasets: [{
      label: 'Average Points per Quarter',
      data: [
        basicStats.pointsPerGame * 0.25,
        basicStats.pointsPerGame * 0.28,
        basicStats.pointsPerGame * 0.22,
        basicStats.pointsPerGame * 0.25,
      ],
      backgroundColor: 'rgba(255, 75, 43, 0.8)',
    }],
  };

  // Scatter Plot: Minutes vs Points
  const scatterData = {
    datasets: [{
      label: 'Minutes vs Production',
      data: [{
        x: detailedStats.minutesPerGame,
        y: basicStats.pointsPerGame,
      }],
      backgroundColor: 'rgba(255, 75, 43, 0.8)',
    }],
  };

  return (
    <Box>
      <Typography variant="h6" gutterBottom sx={{ color: '#FF4B2B', mb: 3 }}>
        Player Analytics Dashboard
      </Typography>
      
      <Grid container spacing={3}>
        {/* Scoring Distribution */}
        <Grid item xs={12} md={6}>
          <Paper sx={{
            p: 3,
            background: 'linear-gradient(to right, #1a1a1a, #2d2d2d)',
            borderRadius: 2,
            border: '1px solid rgba(255, 255, 255, 0.1)',
            height: 300,
          }}>
            <Typography variant="subtitle1" gutterBottom sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
              Scoring Distribution
            </Typography>
            <Pie data={scoringData} options={chartOptions} />
          </Paper>
        </Grid>

        {/* Efficiency Metrics */}
        <Grid item xs={12} md={6}>
          <Paper sx={{
            p: 3,
            background: 'linear-gradient(to right, #1a1a1a, #2d2d2d)',
            borderRadius: 2,
            border: '1px solid rgba(255, 255, 255, 0.1)',
            height: 300,
          }}>
            <Typography variant="subtitle1" gutterBottom sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
              Shooting Efficiency
            </Typography>
            <Line data={efficiencyData} options={chartOptions} />
          </Paper>
        </Grid>

        {/* Points per Quarter */}
        <Grid item xs={12} md={6}>
          <Paper sx={{
            p: 3,
            background: 'linear-gradient(to right, #1a1a1a, #2d2d2d)',
            borderRadius: 2,
            border: '1px solid rgba(255, 255, 255, 0.1)',
            height: 300,
          }}>
            <Typography variant="subtitle1" gutterBottom sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
              Points Distribution by Quarter
            </Typography>
            <Bar data={performanceData} options={chartOptions} />
          </Paper>
        </Grid>

        {/* Minutes vs Production */}
        <Grid item xs={12} md={6}>
          <Paper sx={{
            p: 3,
            background: 'linear-gradient(to right, #1a1a1a, #2d2d2d)',
            borderRadius: 2,
            border: '1px solid rgba(255, 255, 255, 0.1)',
            height: 300,
          }}>
            <Typography variant="subtitle1" gutterBottom sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
              Minutes vs Production
            </Typography>
            <Scatter data={scatterData} options={chartOptions} />
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default PlayerAnalytics;
