import React, { useState, useEffect } from 'react';
import {
  Box,
  TextField,
  Autocomplete,
  Typography,
  Grid,
  Paper,
  Chip,
  CircularProgress,
} from '@mui/material';
import axios from 'axios';
import PlayerStats from './PlayerStats';
import RadarChart from './RadarChart';

const Dashboard = () => {
  const [players, setPlayers] = useState([]);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [playerStats, setPlayerStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Load initial players list
    fetchPlayers();
  }, []);

  useEffect(() => {
    // Fetch filtered players when search term changes
    const delayDebounceFn = setTimeout(() => {
      fetchPlayers(searchTerm);
    }, 300);

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  const fetchPlayers = async (search = '') => {
    try {
      const response = await axios.get(`http://localhost:5000/api/players?search=${search}`);
      setPlayers(response.data);
    } catch (error) {
      console.error('Error fetching players:', error);
    }
  };

  const fetchPlayerStats = async (playerName) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/player/${encodeURIComponent(playerName)}`);
      setPlayerStats(response.data);
    } catch (error) {
      console.error('Error fetching player stats:', error);
    }
    setLoading(false);
  };

  const handlePlayerChange = (event, newValue) => {
    setSelectedPlayer(newValue);
    if (newValue) {
      fetchPlayerStats(newValue);
    } else {
      setPlayerStats(null);
    }
  };

  return (
    <Box>
      <Typography variant="h3" gutterBottom sx={{ color: 'primary.main', fontWeight: 'bold' }}>
        NBA Player Statistics
      </Typography>

      <Paper sx={{ p: 3, mb: 3 }}>
        <Autocomplete
          value={selectedPlayer}
          onChange={handlePlayerChange}
          options={players}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search Player"
              variant="outlined"
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          )}
          loading={loading}
          loadingText="Loading..."
          noOptionsText="No players found"
        />
      </Paper>

      {loading && (
        <Box display="flex" justifyContent="center" my={4}>
          <CircularProgress />
        </Box>
      )}

      {playerStats && !loading && (
        <>
          <Paper sx={{ p: 3, mb: 3 }}>
            <Box display="flex" alignItems="center" gap={2} mb={3}>
              <Typography variant="h4">{playerStats.name}</Typography>
              <Chip label={playerStats.position} color="primary" />
              <Chip label={playerStats.team} color="secondary" />
            </Box>
            <PlayerStats stats={playerStats} />
          </Paper>

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  Player Attribute Radar
                </Typography>
                <RadarChart data={playerStats.radarStats} />
              </Paper>
            </Grid>
          </Grid>
        </>
      )}
    </Box>
  );
};

export default Dashboard;
