import React, { useState } from 'react';
import {
  Box,
  TextField,
  Button,
  Grid,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  MenuItem,
  Chip,
} from '@mui/material';
import axios from 'axios';

const positions = ['', 'G', 'F', 'C', 'PG', 'SG', 'SF', 'PF'];

const AdvancedPlayerSearch = () => {
  const [searchCriteria, setSearchCriteria] = useState({
    min_points: '',
    min_rebounds: '',
    min_assists: '',
    position: '',
    team: '',
  });
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setSearchCriteria(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    try {
      const params = new URLSearchParams();
      Object.entries(searchCriteria).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`http://localhost:5000/api/players/search?${params}`);
      setResults(response.data);
    } catch (error) {
      console.error('Error searching players:', error);
      setError('Failed to search players. Please try again.');
    }
    setLoading(false);
  };

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Advanced Player Search
      </Typography>
      
      <Paper sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6} md={2}>
            <TextField
              fullWidth
              label="Min Points"
              name="min_points"
              type="number"
              value={searchCriteria.min_points}
              onChange={handleChange}
              inputProps={{ min: 0, step: 0.1 }}
            />
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <TextField
              fullWidth
              label="Min Rebounds"
              name="min_rebounds"
              type="number"
              value={searchCriteria.min_rebounds}
              onChange={handleChange}
              inputProps={{ min: 0, step: 0.1 }}
            />
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <TextField
              fullWidth
              label="Min Assists"
              name="min_assists"
              type="number"
              value={searchCriteria.min_assists}
              onChange={handleChange}
              inputProps={{ min: 0, step: 0.1 }}
            />
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <TextField
              fullWidth
              select
              label="Position"
              name="position"
              value={searchCriteria.position}
              onChange={handleChange}
            >
              {positions.map((pos) => (
                <MenuItem key={pos} value={pos}>
                  {pos || 'Any Position'}
                </MenuItem>
              ))}
            </TextField>
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <TextField
              fullWidth
              label="Team"
              name="team"
              value={searchCriteria.team}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <Button
              fullWidth
              variant="contained"
              onClick={handleSearch}
              disabled={loading}
              sx={{ height: '100%' }}
            >
              Search
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}

      {results.length > 0 && (
        <>
          <Typography variant="subtitle1" gutterBottom>
            Showing {results.length} players sorted by points per game
          </Typography>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Rank</TableCell>
                  <TableCell>Player</TableCell>
                  <TableCell>Position</TableCell>
                  <TableCell>Team</TableCell>
                  <TableCell align="right">PPG</TableCell>
                  <TableCell align="right">RPG</TableCell>
                  <TableCell align="right">APG</TableCell>
                  <TableCell align="right">FG%</TableCell>
                  <TableCell align="right">3P%</TableCell>
                  <TableCell align="right">GP</TableCell>
                  <TableCell align="right">MPG</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {results.map((player, index) => (
                  <TableRow 
                    key={player.name}
                    sx={{
                      backgroundColor: index < 3 ? 'rgba(144, 202, 249, 0.08)' : 'inherit',
                    }}
                  >
                    <TableCell>
                      {index + 1}
                      {index < 3 && (
                        <Chip
                          size="small"
                          label={['🥇', '🥈', '🥉'][index]}
                          sx={{ ml: 1 }}
                        />
                      )}
                    </TableCell>
                    <TableCell>
                      <Typography variant="body2" fontWeight={index < 3 ? 'bold' : 'normal'}>
                        {player.name}
                      </Typography>
                    </TableCell>
                    <TableCell>{player.position}</TableCell>
                    <TableCell>{player.team}</TableCell>
                    <TableCell align="right" sx={{ fontWeight: 'bold' }}>
                      {player.points.toFixed(1)}
                    </TableCell>
                    <TableCell align="right">{player.rebounds.toFixed(1)}</TableCell>
                    <TableCell align="right">{player.assists.toFixed(1)}</TableCell>
                    <TableCell align="right">{player.fieldGoalPercentage.toFixed(1)}%</TableCell>
                    <TableCell align="right">{player.threePointPercentage.toFixed(1)}%</TableCell>
                    <TableCell align="right">{player.gamesPlayed}</TableCell>
                    <TableCell align="right">{player.minutesPerGame.toFixed(1)}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </>
      )}
    </Box>
  );
};

export default AdvancedPlayerSearch;
