import React, { useState, useEffect } from 'react';
import {
  Box,
  TextField,
  Autocomplete,
  Typography,
  Grid,
  Paper,
  Chip,
  CircularProgress,
  Alert,
  Tabs,
  Tab,
} from '@mui/material';
import axios from 'axios';
import PlayerStats from './PlayerStats';
import TeamStats from './TeamStats';
import RadarChart from './RadarChart';

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [players, setPlayers] = useState([]);
  const [teams, setTeams] = useState([]);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [playerStats, setPlayerStats] = useState(null);
  const [teamStats, setTeamStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    // Load initial data
    fetchPlayers();
    fetchTeams();
  }, []);

  useEffect(() => {
    // Fetch filtered players when input value changes
    if (activeTab === 0 && inputValue.length > 0) {
      const delayDebounceFn = setTimeout(() => {
        fetchPlayers(inputValue);
      }, 300);

      return () => clearTimeout(delayDebounceFn);
    }
  }, [inputValue, activeTab]);

  const fetchPlayers = async (search = '') => {
    setError(null);
    try {
      const response = await axios.get(`http://localhost:5000/api/players?search=${search}`);
      const playersList = Array.isArray(response.data) ? response.data : [];
      setPlayers(playersList);
    } catch (error) {
      console.error('Error fetching players:', error);
      setError('Failed to fetch players. Please try again.');
      setPlayers([]);
    }
  };

  const fetchTeams = async () => {
    setError(null);
    try {
      const response = await axios.get('http://localhost:5000/api/teams');
      const teamsList = Array.isArray(response.data) ? response.data : [];
      setTeams(teamsList);
    } catch (error) {
      console.error('Error fetching teams:', error);
      setError('Failed to fetch teams. Please try again.');
      setTeams([]);
    }
  };

  const fetchPlayerStats = async (playerName) => {
    if (!playerName) return;
    
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`http://localhost:5000/api/player/${encodeURIComponent(playerName)}`);
      setPlayerStats(response.data);
    } catch (error) {
      console.error('Error fetching player stats:', error);
      setError('Failed to fetch player statistics. Please try again.');
      setPlayerStats(null);
    }
    setLoading(false);
  };

  const fetchTeamStats = async (teamId) => {
    if (!teamId) return;
    
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`http://localhost:5000/api/team/${encodeURIComponent(teamId)}`);
      setTeamStats(response.data);
    } catch (error) {
      console.error('Error fetching team stats:', error);
      setError('Failed to fetch team statistics. Please try again.');
      setTeamStats(null);
    }
    setLoading(false);
  };

  const handlePlayerChange = (event, newValue) => {
    setSelectedPlayer(newValue);
    if (newValue) {
      fetchPlayerStats(newValue);
    } else {
      setPlayerStats(null);
    }
  };

  const handleTeamChange = (event, newValue) => {
    setSelectedTeam(newValue);
    if (newValue) {
      fetchTeamStats(newValue);
    } else {
      setTeamStats(null);
    }
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    setError(null);
    setInputValue('');
    setSelectedPlayer(null);
    setSelectedTeam(null);
    setPlayerStats(null);
    setTeamStats(null);
  };

  return (
    <Box>
      <Typography variant="h3" gutterBottom sx={{ color: 'primary.main', fontWeight: 'bold' }}>
        NBA Statistics Dashboard
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={activeTab}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab label="Player Statistics" />
          <Tab label="Team Statistics" />
        </Tabs>
      </Paper>

      <Paper sx={{ p: 3, mb: 3 }}>
        {activeTab === 0 ? (
          <Autocomplete
            value={selectedPlayer}
            onChange={handlePlayerChange}
            inputValue={inputValue}
            onInputChange={(event, newInputValue) => {
              setInputValue(newInputValue);
            }}
            options={players}
            getOptionLabel={(option) => option || ''}
            isOptionEqualToValue={(option, value) => option === value}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Search Player"
                variant="outlined"
                InputProps={{
                  ...params.InputProps,
                  endAdornment: (
                    <>
                      {loading && <CircularProgress color="inherit" size={20} />}
                      {params.InputProps.endAdornment}
                    </>
                  ),
                }}
              />
            )}
            loading={loading}
            loadingText="Loading..."
            noOptionsText={inputValue.length > 0 ? "No players found" : "Start typing to search"}
            filterOptions={(x) => x}
          />
        ) : (
          <Autocomplete
            value={selectedTeam}
            onChange={handleTeamChange}
            options={teams}
            getOptionLabel={(option) => option || ''}
            isOptionEqualToValue={(option, value) => option === value}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Select Team"
                variant="outlined"
                InputProps={{
                  ...params.InputProps,
                  endAdornment: (
                    <>
                      {loading && <CircularProgress color="inherit" size={20} />}
                      {params.InputProps.endAdornment}
                    </>
                  ),
                }}
              />
            )}
            loading={loading}
            loadingText="Loading..."
            noOptionsText="No teams found"
          />
        )}
      </Paper>

      {loading && (
        <Box display="flex" justifyContent="center" my={4}>
          <CircularProgress />
        </Box>
      )}

      {activeTab === 0 && playerStats && !loading && (
        <>
          <Paper sx={{ p: 3, mb: 3 }}>
            <Box display="flex" alignItems="center" gap={2} mb={3}>
              <Typography variant="h4">{playerStats.name}</Typography>
              <Chip label={playerStats.position} color="primary" />
              <Chip label={playerStats.team} color="secondary" />
            </Box>
            <PlayerStats stats={playerStats} />
          </Paper>

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  Player Attribute Radar
                </Typography>
                <RadarChart data={playerStats.radarStats} />
              </Paper>
            </Grid>
          </Grid>
        </>
      )}

      {activeTab === 1 && teamStats && !loading && (
        <Paper sx={{ p: 3 }}>
          <TeamStats stats={teamStats} />
        </Paper>
      )}
    </Box>
  );
};

export default Dashboard;
